import { createContext, ReactNode, useContext, useState } from "react";
import { api } from "../services/api";


interface CaseProviderProps {
  children: ReactNode;
}

interface Incidents {
  id: string;
  title: string;
  description: string;
  value: string;

}

interface CaseContextData{
  incidents: Incidents[];
  handlerDelete: (id: string) => void;
}

const CaseContext = createContext<CaseContextData>({} as CaseContextData);

export function CaseProvider({children}: CaseProviderProps){
  const [incidents, setIncidents] = useState<Incidents[]>([])

  const ongId = localStorage.getItem('ongId')
  useEffect(() => {
    api.get('profile', {
        headers: {
            Authorization: ongId
        }
    }).then(response => {
        setIncidents(response.data);
    });
}, [ongId]);

  const handlerDelete = async (id: string) => {
    try{
      await api.delete(`incident/${id}`, {
        headers: {
          Authorizatoin: ongId
        }
      })
      const incidentRemove = incidents.filter(incident => incident.id !== id)

      setIncidents(incidentRemove)
    }catch (error) {
      alert('Error ao deleter o caso, tente novamente')
    }
  }

  return(
    <CaseContext.Provider value={{incidents, handlerDelete}}>{children}</CaseContext.Provider>
  )
}

export function useCase(): CaseContextData {
  const context = useContext(CaseContext);

  return context
}
